  <script>
  // WAIT FUNCTION FOR ELEMENT TO POPULATE
  function waitForElement(elementPath, callBack){
    window.setTimeout(function(){
      if($(elementPath).length){
        callBack(elementPath, $(elementPath));
      }else{
        waitForElement(elementPath, callBack);
      }
    },250)
  }
  </script>


