components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    <style>
        .productView-table-bulkPricing,
        .productView-table-bulkPricing th,
        .productView-table-bulkPricing td {
        	background-color: #FFF4D1;
        	border: 1px solid black;
        	font-size: 1rem;
        	font-weight: 900;
        	padding-top: 0.35714rem;
            padding-right: 0.71429rem;
            padding-bottom: 0.35714rem;
            padding-left: 0.71429rem;
        	text-align: center;
        }
        .productView-table-bulkPricing th:last-child,
        .productView-table-bulkPricing td:last-child {
        	color: #d42020;
        }
        .productView-title-bulkPricing {
        	font-size: 1.71429rem;
        	font-weight: 700;
        	text-align: left;
        	text-transform: capitalize;
        }
        .productView-info-bulkPricing {
            align-items: center;
            column-gap: 2rem;
        	font-family: "Roboto", Arial, Helvetica, sans-serif;
        	padding: 3rem 0;
        	width: 100%;
        }
    </style>

    {{!-- 
    <dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>
    <dd class="productView-info-value">
        <a href="{{url}}" data-reveal-id="bulkPricingModal-{{id}}-{{$index}}">
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="bulkPricingModal-{{id}}-{{$index}}" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            <a href="#" class="modal-close" aria-label="Close"><span aria-hidden="true">&#215;</span></a>
        </div>

        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                    <li>
                        {{lang 'products.bulk_pricing.range' min=min max=max}}
                        {{#if type '===' 'percent'}}
                            {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                        {{/if}}
                        {{#if type '===' 'fixed'}}
                            {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                        {{/if}}
                        {{#if type '===' 'price'}}
                            {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                        {{/if}}
                    </li>
                {{/each}}
            </ul>
        </div>
    </div>
    --}}

    <p>&nbsp;</p>
    <h2 class="productView-title-bulkPricing">Quantity Discounts:</h2>

    <table class="productView-table-bulkPricing">
        <tr>
            <th>Quantity</th>
            <th>Price Each</th>
            <th>Savings</th>
        </tr>

        <tr>
            <td>1{{#if (subtract bulk_discount_rates.0.min 1) '>' 1}} - {{subtract bulk_discount_rates.0.min 1}}{{/if}}</td>
            <td>{{price.without_tax.formatted}}</td>
            <td></td>
        </tr>

        {{#each bulk_discount_rates}}
            {{#if type '===' 'percent'}}
                <tr data-custom-type="{{type}}">
                    <td>{{min}}{{#if max '<=' 0}}+{{else}} - {{max}}{{/if}}</td> 
                    <td>{{subtract ../../price.without_tax.value (divide (multiply discount.value ../../price.without_tax.value) 100)}}</td>
                    <td>{{discount.formatted}}</td>
                </tr>
            {{/if}}

            {{#if type '===' 'fixed'}}
                <tr data-custom-type="{{type}}">
                    <td>{{min}}{{#if max '<=' 0}}+{{else}} - {{max}}{{/if}}</td> 
                    <td>${{toFixed (divide (multiply (subtract ../../price.without_tax.value discount.value) 100) ../../price.without_tax.value) 2}}</td>
                    <td>{{round (divide (multiply discount.value 100) ../../price.without_tax.value)}}%</td>
                </tr>
            {{/if}}

            {{#if type '===' 'price'}}
                <tr data-custom-type="{{type}}">
                    <td>{{min}} {{#if max '<=' 0}}+{{else}}- {{max}}{{/if}}</td> 
                    <td>${{toFixed (subtract ../../price.without_tax.value discount.value) 2}}</td>
                    <td>{{round (divide (multiply discount.value 100) ../../price.without_tax.value)}}%</td>
                </tr>
            {{/if}}
        {{/each}}
    </table>
{{/if}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price}}
{{else}}
    {{#if price.with_tax}}
      <div class="msrp-sale-regular-price-section withTax">
        <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax non-sale-price non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
      </div>
        <div class="price-section price-section--withTax current-price{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}" >
            <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>

              <span class="price-label price-now-label" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
              </span>

            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
      <div class="msrp-sale-regular-price-section withoutTax">
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
        {{#if price.non_sale_price_without_tax.formatted}}
        <div class="price-section price-section--withoutTax non-sale-price non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
      </div>
        {{/if}}

        <div class="price-section price-section--withoutTax current-price{{#if price.non_sale_price_without_tax}} sale-price {{else}} regular-price{{/if}}" >
            <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>

              <span class="price-label price-now-label" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
              </span>

            <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>

            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}

    {{/if}}

    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>

    {{/if}}
{{/and}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax">
        {{theme_settings.pdp-retail-price-label}}
        <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.with_tax}}
      <div class="msrp-sale-regular-price-section withTax">
        <div class="price-section price-section--withTax rrp-price--withTax" style="display: none;">
            {{theme_settings.pdp-retail-price-label}}
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
      </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax non-sale-price non-sale-price--withTax" style="display: none;">
        {{theme_settings.pdp-non-sale-price-label}}
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax current-price price-range{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}" >
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-with-tax class="price price--withTax">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}

    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">
        {{theme_settings.pdp-retail-price-label}}
        <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.without_tax}}
      <div class="msrp-sale-regular-price-section withoutTax">
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
            {{theme_settings.pdp-retail-price-label}}
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
      </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax non-sale-price non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
        {{theme_settings.pdp-non-sale-price-label}}
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax current-price price-range{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}}" >
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-without-tax class="price price--withoutTax">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}

    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                 <span data-product-price-saved class="price price--saving">{{price.saved.formatted}}</span>
                 <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
     </div>
{{/if}}

components/products/ratings:
{{#for 1 5}}
